package com.bePractical;

public class DataType_I {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("byte size:"+Byte.SIZE+" bytes");
		System.out.println("short size:"+Short.SIZE+" bytes");
		System.out.println("int size:"+Integer.SIZE+" bytes");
		System.out.println("long size:"+Long.SIZE+" bytes");
		System.out.println("float size:"+Float.SIZE+" bytes");
		System.out.println("double size:"+Double.SIZE+" bytes");
		System.out.println("char size:"+Character.SIZE+" bytes");
		System.out.println("---------------------------------------------------------------");
		System.out.println("Printing range of datatypes");
		System.out.println("byte range :"+Byte.MIN_VALUE+" -> " +Byte.MAX_VALUE);
		System.out.println("byte range :"+Short.MIN_VALUE+" -> " +Short.MAX_VALUE);
		System.out.println("byte range :"+Integer.MIN_VALUE+" -> " +Integer.MAX_VALUE);
		System.out.println("byte range :"+Long.MIN_VALUE+" -> " +Long.MAX_VALUE);
		System.out.println("byte range :"+Float.MIN_VALUE+" -> " +Float.MAX_VALUE);
		System.out.println("byte range :"+Double.MIN_VALUE+" -> " +Double.MAX_VALUE);

	}

}
